// frontend/src/App.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [jobs, setJobs] = useState([]);
  const [search, setSearch] = useState('');
  const [category, setCategory] = useState('');
  const [loading, setLoading] = useState(false);

  const fetchJobs = async () => {
    setLoading(true);
    try {
      const response = await axios.get('http://localhost:3000/jobs', {
        params: { search, category, limit: 10 },
      });
      setJobs(response.data.jobs);
    } catch (error) {
      console.error('Erro ao buscar vagas:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchJobs(); // Busca inicial de vagas ao carregar o app
  }, []);

  return (
    <div style={{ padding: '20px' }}>
      <h1>TechJobsMatch - Vagas de TI Remotas</h1>
      <div style={{ marginBottom: '20px' }}>
        <input
          type="text"
          placeholder="Palavras-chave (ex: developer)"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          style={{ marginRight: '10px', padding: '5px' }}
        />
        <input
          type="text"
          placeholder="Categoria (ex: software-dev)"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
          style={{ marginRight: '10px', padding: '5px' }}
        />
        <button onClick={fetchJobs} style={{ padding: '5px 10px' }}>
          Buscar Vagas
        </button>
      </div>

      {loading ? (
        <p>Carregando vagas...</p>
      ) : (
        <ul>
          {jobs.map((job) => (
            <li key={job.id} style={{ marginBottom: '15px' }}>
              <h2>{job.title}</h2>
              <p>Empresa: {job.company_name}</p>
              <p>Categoria: {job.category}</p>
              <p>Localização: {job.candidate_required_location}</p>
              <p>Salário: {job.salary || 'Não informado'}</p>
              <a
                href={job.url}
                target="_blank"
                rel="noopener noreferrer"
                style={{ color: 'blue', textDecoration: 'underline' }}
              >
                Ver mais detalhes
              </a>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default App;
