import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { 
  Container, TextField, Button, Typography, CircularProgress, Grid, 
  Card, CardContent, CardMedia, CardActions, Link 
} from '@mui/material';

function App() {
  const [jobs, setJobs] = useState([]);
  const [search, setSearch] = useState('');
  const [category, setCategory] = useState('');
  const [loading, setLoading] = useState(false);

  const fetchJobs = async () => {
    setLoading(true);
    try {
      const response = await axios.get('http://localhost:3000/jobs', {
        params: { search, category, limit: 10 },
      });
      setJobs(response.data.jobs);
    } catch (error) {
      console.error('Erro ao buscar vagas:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchJobs();
  }, []);

  return (
    <Container maxWidth="md" style={{ marginTop: '40px' }}>
      <Typography variant="h3" align="center" gutterBottom>
        TechJobsMatch - Vagas de TI Remotas
      </Typography>

      <Grid container spacing={2} justifyContent="center" style={{ marginBottom: '20px' }}>
        <Grid item xs={12} md={5}>
          <TextField
            fullWidth
            label="Palavras-chave (ex: developer)"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            variant="outlined"
          />
        </Grid>
        <Grid item xs={12} md={5}>
          <TextField
            fullWidth
            label="Categoria (ex: software-dev)"
            value={category}
            onChange={(e) => setCategory(e.target.value)}
            variant="outlined"
          />
        </Grid>
        <Grid item xs={12} md={2}>
          <Button 
            fullWidth 
            variant="contained" 
            onClick={fetchJobs} 
            style={{ height: '56px' }}
          >
            Buscar
          </Button>
        </Grid>
      </Grid>

      {loading ? (
        <Grid container justifyContent="center">
          <CircularProgress />
        </Grid>
      ) : (
        <Grid container spacing={3}>
          {jobs.map((job) => (
            <Grid item xs={12} sm={6} key={job.id}>
              <Card>
                {job.company_logo && (
                  <CardMedia
                    component="img"
                    height="140"
                    image={job.company_logo}
                    alt={`${job.company_name} logo`}
                  />
                )}
                <CardContent>
                  <Typography variant="h5" component="div">
                    {job.title}
                  </Typography>
                  <Typography variant="subtitle1" color="text.secondary">
                    {job.company_name} - {job.candidate_required_location}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    {job.salary || 'Salário não informado'}
                  </Typography>
                </CardContent>
                <CardActions>
                  <Button size="small" color="primary">
                    <Link href={job.url} target="_blank" rel="noopener" underline="none">
                      Ver detalhes
                    </Link>
                  </Button>
                </CardActions>
              </Card>
            </Grid>
          ))}
        </Grid>
      )}
    </Container>
  );
}

export default App;
